import React, {Component} from 'react';
import axios from 'axios';

class Profile extends Component {

  constructor(props) {
    super(props);
    this.state = {
      user: false,
    };
  }


  async componentDidMount() {
    /*
    let axiosConfig = {
      headers: {
          "Access-Control-Allow-Origin": "*",
      }
    };
    let userNameData = {"name": localStorage.getItem('token')};
    console.log(userNameData);
    axios.post('http://localhost:5000/profile', userNameData, axiosConfig)
    .then((response) => {
      console.log(response);
      this.setState({user: true});
    }, (error) => {
      console.log('Looks like there was a problem: \n', error);
    });
    */

    let userNameData = {"name": localStorage.getItem('token')};

    fetch('http://localhost:5000/profile', {
      method: 'POST', // or 'PUT'
      headers: {
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*',
      },
      body: JSON.stringify(userNameData),
    })
    .then(response => response.json())
    .then(data => {
      console.log('Success:', data);
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  }

  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="jumbotron col-12">
            <h1 className="display-3">Welcome, {localStorage.getItem('token')}!</h1>
            <h2 className="display-3">Current points: </h2>
          </div>
        </div>
      </div>
    );
  }
}

export default Profile;
