import React, {Component} from 'react';
import axios from 'axios';
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Col from 'react-bootstrap/Col'
import { Redirect } from "react-router-dom";

class NewPost extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      body:'',
      posted: false,
      tagsList: null,
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.addTag = this.addTag.bind(this);
  }

  async componentDidMount() {
      const data = (await axios.get('https://epiphany-test-three.herokuapp.com/main')).data;
      console.log(data.data[0]);
      const tags = data.data[1];
      console.log(arr);
      let tagArr = [];
      for (var j in tags) {
        tagArr.push(tags[j])
      }
      this.setState({
        tagsList: tagArr,
      });
      console.log(this.state.tagsList);
  }

  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value
    });
  }

  addTag(event) {
    if (event.keyCode === 39) {
      let arr = [];
      if (this.state.tags === null){
        arr = [];
      } else {
        arr = this.state.tags;
      }
      arr.push(event.target.value)
      this.setState({
        tags: arr,
      });
    }
    console.log(this.state.tags);
  }

  async handleSubmit(event) {
    let postData = {"title": this.state.title, "body": this.state.body, "user": localStorage.getItem("userEmail")}
    axios.post('https://epiphany-test-three.herokuapp.com/create', postData)
    .then((response) => {
      console.log(response);
      this.setState({
        posted: true,
      })
    }, (error) => {
      console.log('Looks like there was a problem: \n', error);
    });
      event.preventDefault();
  }

  render() {
    const top100Films = this.state.tagsList;
    const redirectTo = this.state.posted;
    if (redirectTo) {
        return (
          <Redirect to="/" />
        );
    }
    return (
      <Col style={{paddingLeft: 200, paddingRight: 200}}>
        <Form onSubmit={this.handleSubmit}>
          <Form.Group controlId="formGroupTitle">
              <Form.Label>Title</Form.Label>
              <Form.Control type="text" name="title" placeholder="Enter title" title={this.state.title} onChange={this.handleChange} />
          </Form.Group>
          <div className="row">
            <Row style={{paddingLeft: 25}}>
            {this.state.tags && this.state.tags.map(tag => (
              <ButtonGroup style={{ paddingLeft: 2, paddingRight: 2, paddingTop: 2, paddingBottom: 5}} aria-label="Basic example">
                <Button onClick={this.deleteTag} id={tag} variant="secondary">x</Button>
                <Button variant="secondary">{tag}</Button>
              </ButtonGroup>
              ))
          }
            </Row>
          </div>
          <div className="row">
          {this.state.tagsList && <div style={{ width: 1125, paddingLeft: 15 }}>
          <Autocomplete
            id="free-solo-demo"
            freeSolo
            options={top100Films.map((option) => option.name)}
            renderInput={(params) => (
              <TextField {...params}  id="standard-full-width" label="Search with tags!" margin="normal" variant="outlined" onKeyUp={this.addTag}/>
            )}
          />
          </div>
        }
          </div>
            <Form.Group controlId="formGroupPassword">
              <Form.Label>Body</Form.Label>
              <Form.Control as="textarea" rows="15" name="body" placeholder="Body" body={this.state.body} onChange={this.handleChange}/>
          </Form.Group>
          <Button variant="success" type="submit">Submit</Button>
        </Form>
      </Col>
    );
  }
}

export default NewPost;
