import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';
import Truncate from 'react-truncate';
import Badge from 'react-bootstrap/Badge'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Spinner from 'react-bootstrap/Spinner'
import Card from 'react-bootstrap/Card'
import Container from 'react-bootstrap/Container'
import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'

class Posts extends Component {

    constructor(props) {
      super(props);

      this.state = {
        list: null,
      };
    }

    async componentDidMount() {
        let loginData = {"email": localStorage.getItem("userEmail")}
        console.log(loginData)
        axios.post('http://localhost:5000/fav', loginData)
        .then((response) => {
            const lists = response.data.info[0];
            let arr = [];
            for (var i in lists) {
              arr.push(list[i])
            }
            this.setState({
              list: arr,
            });
        }, (error) => {
            console.log('Looks like there was a problem: \n', error);
        });
    }

    colors = ["#ffbaba","#ffddab","#fdffcf","#bdffb3","#b8fff9","#ffd1ea","#edc4ff"];
    getColor(){
      if (this.colors.length === 0) {
        this.colors = ["#ffbaba","#ffddab","#fdffcf","#bdffb3","#b8fff9","#ffd1ea","#edc4ff"];
      }
      var len = this.colors.length;
      var randomNum = Math.floor(Math.random()*len);
      var color = this.colors[randomNum];
      this.colors.splice(randomNum, 1);
      return color;
    }

    followedUsers = () => {

    }

    render() {
      let first = 0;
      function myKey() {
        first = first + 1;
        return first;
      }
      return (
        <Container className="justify-content-md-center">
          {this.state.list === null && <div> <Spinner animation="border" variant="primary" /> <p>Loading posts...</p></div>}
          <Tabs defaultActiveKey="profile" id="uncontrolled-tab-example">
            {this.state.list && this.state.list.map(thing => (
              <Tab title={ thing.username }>
                {thing.post_list && thing.post_list.map(another => (
                  <div key={another.id} className="col-sm-12 col-md-4 col-lg-3">
                  <Link to={`/post/${another.id}`}>
                    <Card style={{backgroundColor: this.getColor(), color: "#161717", height: '250px'}}>
                      <Card.Body>
                        <Card.Title>{another.title}</Card.Title>
                        <Card.Text style={{maxLength: "100"}}>
                          <Truncate lines={1}>
                            {another.body}
                          </Truncate>
                        </Card.Text>
                      </Card.Body>
                    </Card>
                  </Link>
                </div>
                ))
                }
              </Tab>
            ))          
            }
          </Tabs>






          {this.state.posts && this.state.posts.map(post => (
            <Row className="justify-content-md-center">
                <Card key={post.id} style={{backgroundColor: this.getColor(), marginTop: 10, marginBottom: 10, alignItems: "center", width: '50rem'}}>
                <Link key={myKey()} to={`/post/${post.id}`}>
                    <Card.Body>
                        <Card.Title style={{color: "#161717", textAlign: "center"}}>{post.title}</Card.Title>
                        <Row className="justify-content-md-center">
                        {post.tags && post.tags.map(tag => (
                        <Col key={myKey()} style={{ paddingLeft: 2, paddingRight: 2, alignItems: "center" }} md="auto">
                            <Badge variant="info">{tag.name}</Badge>
                        </Col>
                        ))
                        }
                        </Row>
                        {post.is_file === false &&
                        <Card.Text style={{color: "#161717", textAlign: "center"}}>
                        <Truncate lines={2}>
                            {post.body}
                        </Truncate>
                        </Card.Text>
                    }
                    {post.is_file === true &&
                        <div>
                        <video id="samp" width="540" height="380" controls>
                            <source src={`./static/${post.body}`} type="video/mp4">
                            </source>
                            Your browser does not support this video format.
                        </video>
                        </div>
                    }
                    </Card.Body>
                    </Link>
                    <Card.Text className="mb-3">
                        <Link key={myKey()} to={`/users/${post.user_id}`}>
                        <small style={{color: "#323336"}}>Posted by {post.user} at {post.time}</small>
                        </Link>
                    </Card.Text>
                </Card>
              </Row>
            ))
          }
        </Container>
      )
    }
  }

  export default Posts;
