import React, {Component, Fragment} from 'react';

class Comment extends Component {
  constructor(props) {
    super(props);
    this.state = {
      comment: '',
    };
  }

  updateComment(value) {
    this.setState({
      comment: value,
    });
  }

  submit() {
    this.props.submitComment(this.state.comment);
    this.setState({
      comment: '',
    });
  }

  render() {
    return (
      <Fragment>
        <div className="form-group text-center">
          <label htmlFor="exampleInputEmail1">Comment on this post</label>
          <input
            type="text"
            onChange={(e) => {this.updateComment(e.target.value)}}
            className="form-control"
            placeholder="Comment away!"
            value={this.state.comment}
          />
        </div>
        <button
          className="btn btn-primary"
          onClick={() => {this.submit()}}>
          Submit
        </button>
        <hr className="my-4" />
      </Fragment>
    )
  }
}

export default Comment;
